                public static DialogResult AddMessageTaskToQueue(string string_MessageText_obj, string string_CaptionText_obj, MessageBoxButtons messageBoxButtons_obj, MessageBoxIcon messageBoxIcon_obj)
                {
                    MessageBoxPendingPool messageBoxPendingPool_obj = new MessageBoxPendingPool();

                    messageBoxPendingPool_obj.MessageText = string_MessageText_obj;
                    messageBoxPendingPool_obj.CaptionText = string_CaptionText_obj;

                    messageBoxPendingPool_obj.ButtonsIndex = 0;
                    messageBoxPendingPool_obj.IconIndex = 0;

                    switch (messageBoxButtons_obj)
                    {
                        case MessageBoxButtons.AbortRetryIgnore:
                            messageBoxPendingPool_obj.ButtonsIndex = 0;
                            break;

                        case MessageBoxButtons.OK:
                            messageBoxPendingPool_obj.ButtonsIndex = 1;
                            break;

                        case MessageBoxButtons.OKCancel:
                            messageBoxPendingPool_obj.ButtonsIndex = 2;
                            break;

                        case MessageBoxButtons.RetryCancel:
                            messageBoxPendingPool_obj.ButtonsIndex = 3;
                            break;

                        case MessageBoxButtons.YesNo:
                            messageBoxPendingPool_obj.ButtonsIndex = 4;
                            break;

                        case MessageBoxButtons.YesNoCancel:
                            messageBoxPendingPool_obj.ButtonsIndex = 5;
                            break;
                    }

                    switch (messageBoxIcon_obj)
                    {
                        case MessageBoxIcon.Asterisk:
                            messageBoxPendingPool_obj.IconIndex = 0;
                            break;

                        case MessageBoxIcon.Error:
                            messageBoxPendingPool_obj.IconIndex = 1;
                            break;

                        case MessageBoxIcon.Exclamation:
                            messageBoxPendingPool_obj.IconIndex = 2;
                            break;

                        case MessageBoxIcon.None:
                            messageBoxPendingPool_obj.IconIndex = 5;
                            break;

                        case MessageBoxIcon.Question:
                            messageBoxPendingPool_obj.IconIndex = 6;
                            break;
                    }

                    messageBoxPendingPool_obj.TaskExecutionCompletedFlag = false;

                    MessageBoxPendingPoolContainer.Add(messageBoxPendingPool_obj);

                    while (messageBoxPendingPool_obj.bool_TaskExecutionCompletedFlag == false)
                    {
                        Thread.Sleep(1000);
                    }

                    return messageBoxPendingPool_obj.DialogResult_UserSelection;
                }

