            void SendAvailableDisplayModes()
            {
                try
                {
                    MemoryStream memoryStream_DataToSend = new MemoryStream();

                    CommonMethods.WriteIntToStream(memoryStream_DataToSend, 25);

                    int int_ElementsCount = 0;

                    CommonMethods.WriteIntToStream(memoryStream_DataToSend, 0);

                    DEVMODE devMode = new DEVMODE();

                    devMode.dmDeviceName = new string(new char[32]);
                    devMode.dmFormName = new string(new char[32]);
                    devMode.dmSize = (short)Marshal.SizeOf(devMode);

                    for (int int_CycleCount = -1; ; int_CycleCount++)
                    {
                        if (EnumDisplaySettings(null, int_CycleCount, ref devMode) == 0) break;

                        if (devMode.dmPelsWidth >= 640 && devMode.dmPelsHeight >= 480 &&
                            devMode.dmBitsPerPel >= 8 && devMode.dmDisplayFrequency >= 60)
                        {
                            int_ElementsCount++;

                            CommonMethods.WriteIntToStream(memoryStream_DataToSend, devMode.dmPelsWidth);
                            CommonMethods.WriteIntToStream(memoryStream_DataToSend, devMode.dmPelsHeight);
                            CommonMethods.WriteIntToStream(memoryStream_DataToSend, devMode.dmBitsPerPel);
                            CommonMethods.WriteIntToStream(memoryStream_DataToSend, devMode.dmDisplayFrequency);
                        }
                    }

                    int_ElementsCount--;

                    memoryStream_DataToSend.SetLength(memoryStream_DataToSend.Position);

                    memoryStream_DataToSend.Position = 4;

                    CommonMethods.WriteIntToStream(memoryStream_DataToSend, int_ElementsCount);

                    memoryStream_DataToSend.Position = memoryStream_DataToSend.Length;

                    CurrentlyUsedTcpClient.SendData(memoryStream_DataToSend.ToArray(), SentDataType.ApplicationData);

                    memoryStream_DataToSend.Close();
                }
                catch
