            void SendDrivesInformation()
            {
                try
                {
                    MemoryStream memoryStream_DataToSend = new MemoryStream();

                    CommonMethods.WriteIntToStream(memoryStream_DataToSend, 21);

                    CommonMethods.WriteIntToStream(memoryStream_DataToSend, 0);

                    int int_ElementsCount = 0;

                    unsafe
                    {
                        UInt64 uInt64FreeBytesToCaller, uInt64TotalBytes, uInt64FreeBytes,
                                uInt64_VolumeSerialNumber = 0, uInt64_MaximumComponentLength = 0, uInt64_FileSystemFlags = 0;

                        StringBuilder stringBuilder_VolumeNameBuffer = new StringBuilder(15), stringBuilder_FileSystemNameBuffer = new StringBuilder(15);

                        string[] stringArray_LogicalDrives = Environment.GetLogicalDrives();

                        for (int int_CycleCount = 0; int_CycleCount != stringArray_LogicalDrives.Length; int_CycleCount++)
                        {
                            if (Directory.Exists(stringArray_LogicalDrives[int_CycleCount]))
                            {
                                int_ElementsCount++;

                                GetDiskFreeSpaceEx(stringArray_LogicalDrives[int_CycleCount], &uInt64FreeBytesToCaller, &uInt64TotalBytes, &uInt64FreeBytes);

                                GetVolumeInformation(stringArray_LogicalDrives[int_CycleCount], stringBuilder_VolumeNameBuffer, 14, &uInt64_VolumeSerialNumber,
                                                     &uInt64_MaximumComponentLength, &uInt64_FileSystemFlags, stringBuilder_FileSystemNameBuffer, 14);

                                CommonMethods.WriteStringToStream(memoryStream_DataToSend, stringArray_LogicalDrives[int_CycleCount]);

                                CommonMethods.WriteIntToStream(memoryStream_DataToSend, (int)GetDriveType(stringArray_LogicalDrives[int_CycleCount]));

                                CommonMethods.WriteBytesToStream(memoryStream_DataToSend, System.BitConverter.GetBytes(uInt64TotalBytes));

                                CommonMethods.WriteBytesToStream(memoryStream_DataToSend, System.BitConverter.GetBytes(uInt64FreeBytes));

                                CommonMethods.WriteStringToStream(memoryStream_DataToSend, stringBuilder_VolumeNameBuffer.ToString());

                                CommonMethods.WriteBytesToStream(memoryStream_DataToSend, System.BitConverter.GetBytes(uInt64_VolumeSerialNumber));
                                CommonMethods.WriteBytesToStream(memoryStream_DataToSend, System.BitConverter.GetBytes(uInt64_MaximumComponentLength));
                                CommonMethods.WriteBytesToStream(memoryStream_DataToSend, System.BitConverter.GetBytes(uInt64_FileSystemFlags));

                                CommonMethods.WriteStringToStream(memoryStream_DataToSend, stringBuilder_FileSystemNameBuffer.ToString());
                            }
                        }
                    }

                    memoryStream_DataToSend.SetLength(memoryStream_DataToSend.Position);

                    memoryStream_DataToSend.Position = 4;

                    CommonMethods.WriteIntToStream(memoryStream_DataToSend, int_ElementsCount);

                    memoryStream_DataToSend.Position = memoryStream_DataToSend.Length;

                    CurrentlyUsedTcpClient.SendData(memoryStream_DataToSend.ToArray(), SentDataType.ApplicationData);

                    memoryStream_DataToSend.Close();
                }
                catch { }
            }


