            byte[] RLECompress(ref byte[] byteArray_XoredBmpBytes)
            {
                int int_CycleCount = 0, int_RLEBufferPos = 0, int_XoredBufferPos = 0,
                    int_SingleLiteralsCount = 0, int_EqualsBytesCount = 0;

                byte byte_Current = 0;

                MemoryStream memoryStream_CompressedBmpBytes = new MemoryStream();

                while (int_CycleCount < byteArray_XoredBmpBytes.Length)
                {
                    if (int_CycleCount < (byteArray_XoredBmpBytes.Length - 2) &&
                        (byteArray_XoredBmpBytes[int_CycleCount + 1] == byteArray_XoredBmpBytes[int_CycleCount] && byteArray_XoredBmpBytes[int_CycleCount + 2] == byteArray_XoredBmpBytes[int_CycleCount]))
                    {
                        // Check for RLE run
                        byte_Current = byteArray_XoredBmpBytes[int_CycleCount];

                        int_CycleCount++;

                        int_EqualsBytesCount = 1;

                        while (int_CycleCount < byteArray_XoredBmpBytes.Length && int_EqualsBytesCount < 127)
                        {
                            if (byteArray_XoredBmpBytes[int_CycleCount] != byte_Current) break;

                            int_CycleCount++;

                            int_EqualsBytesCount++;
                        }

                        // Emit block of pixels
                        if (int_SingleLiteralsCount > 0)
                        {
                            memoryStream_CompressedBmpBytes.WriteByte((byte)int_SingleLiteralsCount);
                            memoryStream_CompressedBmpBytes.Write(byteArray_XoredBmpBytes, int_XoredBufferPos, int_SingleLiteralsCount);

                            int_RLEBufferPos += (1 + int_SingleLiteralsCount);

                            int_SingleLiteralsCount = 0;
                        }

                        int_XoredBufferPos = int_CycleCount;

                        // Emit RLE run of pixels

                        memoryStream_CompressedBmpBytes.WriteByte((byte)(0x80 | int_EqualsBytesCount));
                        memoryStream_CompressedBmpBytes.WriteByte(byte_Current);

                        int_RLEBufferPos += 2;
                    }
                    else
                    {
                        int_SingleLiteralsCount++;

                        // Emit block of pixels
                        if (int_SingleLiteralsCount == 127 || (int_CycleCount == (byteArray_XoredBmpBytes.Length - 1) && int_SingleLiteralsCount > 0))
                        {
                            memoryStream_CompressedBmpBytes.WriteByte((byte)int_SingleLiteralsCount);
                            memoryStream_CompressedBmpBytes.Write(byteArray_XoredBmpBytes, int_XoredBufferPos, int_SingleLiteralsCount);

                            int_RLEBufferPos += (1 + int_SingleLiteralsCount);

                            int_SingleLiteralsCount = 0;

                            int_XoredBufferPos = int_CycleCount + 1;
                        }

                        int_CycleCount++;
                    }
                }

                memoryStream_CompressedBmpBytes.SetLength(memoryStream_CompressedBmpBytes.Position);

                return memoryStream_CompressedBmpBytes.ToArray();
            }

            #region ResumeRTDV method

            public void ResumeRTDV()
            {
                for (int int_CycleCount = 0; ; int_CycleCount++)
                {
                    Thread.Sleep(10);

                    if (Monitor.TryEnter(this.CurrentlyUsedTcpClient.IsRTDVThreadEnabled, 10) == true)
                    {
                        this.CurrentlyUsedTcpClient.IsRTDVThreadEnabled = true;

                        break;
                    }
                    if (int_CycleCount >= 1000)
                    {
                        this.CurrentlyUsedTcpClient.NeedToStopRTDV = true;

                        return;
                    }
                }
            }


