            #region StartRTDV method

            public void StartRTDV()
            {
                try
                {
                    MemoryStream memoryStream_ReceivedData = new MemoryStream(byteArray_ReceivedData, 4, byteArray_ReceivedData.Length - 4);

                    int int_MaxUpdatesPerSecond = CommonMethods.ReadIntFromStream(memoryStream_ReceivedData),
                        int_ImageCodingAlgorithm = CommonMethods.ReadIntFromStream(memoryStream_ReceivedData),
                        int_RTDVRegionsCount = CommonMethods.ReadIntFromStream(memoryStream_ReceivedData);

                    int int_DeflateLength = 0;

                    byte[] byteArray_CurrentRegion = null, byteArray_PreviousRegion = null, byteArray_XORedRegion = null;

                    MemoryStream memoryStream_DataToSend = new MemoryStream(),
                                 memoryStream_XORedPngRegion = new MemoryStream();

                    memoryStream_DataToSend.SetLength(0);


                    MemoryStream memoryStream_CompressdBitmap = new MemoryStream();

                    DeflateStream deflateStream_CompressedBitmap = new DeflateStream(memoryStream_CompressdBitmap, CompressionMode.Compress, true);



                    byte[][] byteMultiDimArray_PreviousRegions = new byte[64][];

                    Bitmap bitmap_XORedInPng = null;

                    BitmapData bitmapData_XORedInPng;

                    IntPtr intPtr_BitmapPixelPtr;

                    int int_RegionWidth = 0, int_RegionHeight = 0, int_RectangeYPoint = 0,
                        int_CopyStartIndex = 0, int_RegionIndex = 0, int_BytesPerPixel = 0,
                        int_PreviousScreenBitsPerPixel = 0, int_RegionPortion = int_RTDVRegionsCount;

                    bool bool_IsFirstFrame = true, bool_AreRegionsIdentity = true;

                    if (this.CurrentlyUsedTcpClient.RTDVQueries != 0)
                    {
                        this.CurrentlyUsedTcpClient.RTDVQueries++;

                        return;
                    }

                    this.CurrentlyUsedTcpClient.RTDVQueries++;

                    this.CurrentlyUsedTcpClient.NeedToStopRTDV = false;

                    ProcWindowService.RTDVContainer.NeedToRefreshFlag = true;

                    while (ProcWindowService.RTDVContainer.RefreshCompleteFlag == false)
                    {
                        ProcWindowService.RTDVContainer.NeedToRefreshFlag = true;

                        Thread.Sleep(50);

                        if (this.CurrentlyUsedTcpClient.NeedToStopRTDV == true) return;

                        continue;
                    }


                    int_PreviousScreenBitsPerPixel = ProcWindowService.RTDVContainer.ScreenBitsPerPixel;

                    int_BytesPerPixel = ProcWindowService.RTDVContainer.ScreenBitsPerPixel / 8;

                    PixelFormat pixelFormat_CurrentFormat = PixelFormat.Format24bppRgb;

                    #region Pixel Format initialization

                    switch (Screen.PrimaryScreen.BitsPerPixel)
                    {
                        case 32:
                            pixelFormat_CurrentFormat = PixelFormat.Format32bppArgb;
                            break;

                        case 24:
                            pixelFormat_CurrentFormat = PixelFormat.Format24bppRgb;
                            break;

                        case 16:
                            pixelFormat_CurrentFormat = PixelFormat.Format16bppRgb555;
                            break;
                    }

                    #endregion

                    ScreenCaptureEnvironment.RTDVClientsCount++;

                    while (this.CurrentlyUsedTcpClient != null && this.CurrentlyUsedTcpClient.NeedToStopRTDV != true)
                    {
                        if (this.CurrentlyUsedTcpClient.NeedToStopRTDV == true)
                        {
                            return;
                        }

                        if (ProcWindowService.RTDVContainer.RefreshCompleteFlag == false)
                        {
                            ProcWindowService.RTDVContainer.NeedToRefreshFlag = true;

                            Thread.Sleep(50);

                            if (this.CurrentlyUsedTcpClient.NeedToStopRTDV == true) return;

                            continue;
                        }


                        else
                        {
                            if (1000 / int_MaxUpdatesPerSecond < ScreenCaptureEnvironment.RTDVSleepValue)
                            {
                                ScreenCaptureEnvironment.RTDVSleepValue = 1000 / int_MaxUpdatesPerSecond;
                            }

                            #region Write base information to memoryStream_DataToSend

                            memoryStream_DataToSend.SetLength(0);

                            CommonMethods.WriteIntToStream(memoryStream_DataToSend, 37);

                            memoryStream_DataToSend.Write(BitConverter.GetBytes((Int16)ProcWindowService.RTDVContainer.ScreenWidthSize), 0, 2);
                            memoryStream_DataToSend.Write(BitConverter.GetBytes((Int16)ProcWindowService.RTDVContainer.ScreenHeightSize), 0, 2);

                            memoryStream_DataToSend.Write(BitConverter.GetBytes((Int16)GetMouseCursorPositionX()), 0, 2);
                            memoryStream_DataToSend.Write(BitConverter.GetBytes((Int16)GetMouseCursorPositionY()), 0, 2);

                            memoryStream_DataToSend.WriteByte((byte)ProcWindowService.RTDVContainer.ScreenBitsPerPixel);

                            memoryStream_DataToSend.WriteByte((byte)int_RTDVRegionsCount);

                            #endregion

                            ///////////////////////////////////!!!

                            if (ProcWindowService.RTDVContainer.RTDVScreenShot == null || ProcWindowService.RTDVContainer.RTDVScreenShot.Length < 614400 || ProcWindowService.RTDVContainer.RefreshCompleteFlag == false)
                            {
                                Thread.Sleep(50);

                                if (this.CurrentlyUsedTcpClient.NeedToStopRTDV == true) return;

                                continue;
                            }

                            unsafe
                            {
                                fixed (byte* bytePointer_RTDVScreenShot = ProcWindowService.RTDVContainer.RTDVScreenShot)
                                {
                                    intPtr_BitmapPixelPtr = (IntPtr)bytePointer_RTDVScreenShot;
                                }
                            }

                            //////////////////////////////////////

                            #region null-objects initialization

                            if (byteArray_CurrentRegion == null || bool_IsFirstFrame == true || byteArray_PreviousRegion == null || byteArray_XORedRegion == null || byteArray_CurrentRegion.Length == 0)
                            {
                                int_RegionWidth = ProcWindowService.RTDVContainer.ScreenWidthSize / int_RegionPortion;
                                int_RegionHeight = ProcWindowService.RTDVContainer.ScreenHeightSize / int_RegionPortion;

                                int_BytesPerPixel = ProcWindowService.RTDVContainer.ScreenBitsPerPixel / 8;

                                byteArray_CurrentRegion = new byte[(int_RegionWidth * int_RegionHeight) * int_BytesPerPixel];

                                byteArray_PreviousRegion = new byte[(int_RegionWidth * int_RegionHeight) * int_BytesPerPixel];
                                byteArray_XORedRegion = new byte[(int_RegionWidth * int_RegionHeight) * int_BytesPerPixel];
                            }

                            #endregion

                            int_RegionIndex = int_CopyStartIndex = 0;

                            #region First Frame processing

                            if (bool_IsFirstFrame == true)
                            {
                                bool_IsFirstFrame = false;

                                for (int int_CycleCount = 0; int_CycleCount != int_RegionPortion; int_CycleCount++)
                                {
                                    int_RectangeYPoint = int_RegionHeight * int_CycleCount;

                                    int_RectangeYPoint = int_RectangeYPoint * ProcWindowService.RTDVContainer.ScreenWidthSize;

                                    for (int int_SubCycleCount = 0; int_SubCycleCount != int_RegionPortion; int_SubCycleCount++)
                                    {
                                        int_CopyStartIndex = int_RectangeYPoint + (int_SubCycleCount * int_RegionWidth);

                                        for (int int_SecondSubCycleCount = 0; int_SecondSubCycleCount != int_RegionHeight; int_SecondSubCycleCount++)
                                        {   //!!!Error!     !     !!!
                                            Array.Copy(ProcWindowService.RTDVContainer.RTDVScreenShot, (int)int_CopyStartIndex * int_BytesPerPixel, byteArray_CurrentRegion, (int_SecondSubCycleCount * int_RegionWidth) * int_BytesPerPixel, int_RegionWidth * int_BytesPerPixel);

                                            int_CopyStartIndex += ProcWindowService.RTDVContainer.ScreenWidthSize;
                                        }

                                        byteMultiDimArray_PreviousRegions[int_RegionIndex] = new byte[byteArray_CurrentRegion.Length];

                                        Array.Copy(byteArray_CurrentRegion, 0, byteMultiDimArray_PreviousRegions[int_RegionIndex], 0, byteArray_CurrentRegion.Length);

                                        int_RegionIndex++;
                                    }
                                }

                                memoryStream_DataToSend.WriteByte(0); // 0 - Is A First Frame

                                #region Compress First Frame

                                deflateStream_CompressedBitmap = new DeflateStream(memoryStream_CompressdBitmap, CompressionMode.Compress, true);

                                deflateStream_CompressedBitmap.Write(ProcWindowService.RTDVContainer.RTDVScreenShot, 0, ProcWindowService.RTDVContainer.RTDVScreenShot.Length);

                                deflateStream_CompressedBitmap.Close();

                                CommonMethods.WriteIntToStream(memoryStream_DataToSend, ProcWindowService.RTDVContainer.RTDVScreenShot.Length);

                                CommonMethods.WriteBytesToStream(memoryStream_DataToSend, memoryStream_CompressdBitmap.ToArray());

                                memoryStream_CompressdBitmap.SetLength(0);

                                #endregion

                                #region Send Data and wait for start event

                                this.CurrentlyUsedTcpClient.IsRTDVThreadEnabled = false;

                                this.CurrentlyUsedTcpClient.SendData(memoryStream_DataToSend.ToArray(), SentDataType.SystemData);
                          
                                for (int int_CycleCount = 0; ; int_CycleCount++)
                                {
                                    Thread.Sleep(10);

                                    if (this.CurrentlyUsedTcpClient.NeedToStopRTDV == true) return;

                                    if (Monitor.TryEnter(this.CurrentlyUsedTcpClient.IsRTDVThreadEnabled, 10) == true)
                                    {
                                        if (this.CurrentlyUsedTcpClient.IsRTDVThreadEnabled == false)
                                        {
                                            continue;
                                        }
                                        if (int_CycleCount * 10 < 1000 / int_MaxUpdatesPerSecond)
                                        {
                                            Thread.Sleep((1000 / int_MaxUpdatesPerSecond) - (int_CycleCount * 10));
                                        }

                                        break;
                                    }

                                    if (int_CycleCount >= 3000)
                                    {
                                        this.CurrentlyUsedTcpClient.NeedToStopRTDV = true;

                                        return;
                                    }
                                }

                                #endregion

                                continue; //continue root while cycle
                            }

                            #endregion

                            bool_AreRegionsIdentity = true;

                            memoryStream_DataToSend.WriteByte(1); // 0 - Is a "NEXT" Frame

                            #region NEXT frame processing

                            for (int int_CycleCount = 0; int_CycleCount != int_RegionPortion; int_CycleCount++)
                            {
                                int_RectangeYPoint = int_RegionHeight * int_CycleCount;

                                int_RectangeYPoint = int_RectangeYPoint * ProcWindowService.RTDVContainer.ScreenWidthSize;

                                for (int int_SubCycleCount = 0; int_SubCycleCount != int_RegionPortion; int_SubCycleCount++)
                                {
                                    bool_AreRegionsIdentity = true;

                                    int_CopyStartIndex = int_RectangeYPoint + (int_SubCycleCount * int_RegionWidth);

                                    byteArray_PreviousRegion = byteMultiDimArray_PreviousRegions[int_RegionIndex];

                                    for (int int_SecondSubCycleCount = 0; int_SecondSubCycleCount != int_RegionHeight; int_SecondSubCycleCount++)
                                    {   //!!Error!
                                        Marshal.Copy((IntPtr)(intPtr_BitmapPixelPtr.ToInt64() + (int_CopyStartIndex * int_BytesPerPixel)), byteArray_CurrentRegion, (int_SecondSubCycleCount * int_RegionWidth) * int_BytesPerPixel, int_RegionWidth * int_BytesPerPixel);

                                        int_CopyStartIndex += ProcWindowService.RTDVContainer.ScreenWidthSize;
                                    }

                                    //compare and XOR  previous and current regions
                                    for (int int_ThirdSubCycleCount = 0; int_ThirdSubCycleCount != byteArray_CurrentRegion.Length; int_ThirdSubCycleCount++)
                                    {
                                        if (byteArray_CurrentRegion[int_ThirdSubCycleCount] == byteArray_PreviousRegion[int_ThirdSubCycleCount])
                                        {
                                            byteArray_XORedRegion[int_ThirdSubCycleCount] = 0;
                                        }
                                        else
                                        {
                                            byteArray_XORedRegion[int_ThirdSubCycleCount] = (byte)(byteArray_CurrentRegion[int_ThirdSubCycleCount] ^ byteArray_PreviousRegion[int_ThirdSubCycleCount]);

                                            byteArray_PreviousRegion[int_ThirdSubCycleCount] = byteArray_CurrentRegion[int_ThirdSubCycleCount];

                                            bool_AreRegionsIdentity = false;
                                        }
                                    }

                                    if (bool_AreRegionsIdentity == false)
                                    {
                                        if (int_ImageCodingAlgorithm == 0)
                                        {
                                            memoryStream_DataToSend.WriteByte(2); // Region XORed and compressed using RLE algorithm

                                            CommonMethods.WriteBytesToStream(memoryStream_DataToSend, this.RLECompress(ref byteArray_XORedRegion));
                                        }

                                        if (int_ImageCodingAlgorithm == 1)
                                        {
                                            memoryStream_DataToSend.WriteByte(3);

                                            memoryStream_CompressdBitmap.SetLength(0);

                                            deflateStream_CompressedBitmap = new DeflateStream(memoryStream_CompressdBitmap, CompressionMode.Compress, true);

                                            deflateStream_CompressedBitmap.Write(byteArray_XORedRegion, 0, byteArray_XORedRegion.Length);

                                            deflateStream_CompressedBitmap.Close();

                                            memoryStream_CompressdBitmap.SetLength(memoryStream_CompressdBitmap.Position);

                                            int_DeflateLength = (int)memoryStream_CompressdBitmap.Length;

                                            CommonMethods.WriteBytesToStream(memoryStream_DataToSend, memoryStream_CompressdBitmap.ToArray());

                                            memoryStream_CompressdBitmap.SetLength(0);
                                        }

                                        if (int_ImageCodingAlgorithm == 2)
                                        {
                                            memoryStream_DataToSend.WriteByte(4);

                                            if (bitmap_XORedInPng == null)
                                            {
                                                bitmap_XORedInPng = new Bitmap(int_RegionWidth, int_RegionHeight, pixelFormat_CurrentFormat);
                                            }

                                            bitmapData_XORedInPng = bitmap_XORedInPng.LockBits(new Rectangle(0, 0, int_RegionWidth, int_RegionHeight), ImageLockMode.ReadWrite, pixelFormat_CurrentFormat);

                                            Marshal.Copy(byteArray_XORedRegion, 0, bitmapData_XORedInPng.Scan0, byteArray_XORedRegion.Length);

                                            bitmap_XORedInPng.UnlockBits(bitmapData_XORedInPng);

                                            bitmap_XORedInPng.Save(memoryStream_XORedPngRegion, ImageFormat.Png);

                                            CommonMethods.WriteBytesToStream(memoryStream_DataToSend, memoryStream_XORedPngRegion.ToArray());

                                            memoryStream_XORedPngRegion.SetLength(0);

                                        }
                                    }
                                    else
                                    {
                                        memoryStream_DataToSend.WriteByte(1); // No changes in this region
                                    }

                                    int_RegionIndex++;
                                }
                            }

                            #endregion

                            ProcWindowService.RTDVContainer.NeedToRefreshFlag = true;
                            ProcWindowService.RTDVContainer.RefreshCompleteFlag = false;

                            #region Send Data and wait for start event

                            this.CurrentlyUsedTcpClient.IsRTDVThreadEnabled = false;

                            this.CurrentlyUsedTcpClient.SendData(memoryStream_DataToSend.ToArray(), SentDataType.ScreenShot);
                      
                            for (int int_CycleCount = 0; ; int_CycleCount++)
                            {
                                Thread.Sleep(10);

                                if (this.CurrentlyUsedTcpClient.NeedToStopRTDV == true) return;

                                if (Monitor.TryEnter(this.CurrentlyUsedTcpClient.IsRTDVThreadEnabled, 10) == true)
                                {
                                    if (this.CurrentlyUsedTcpClient.IsRTDVThreadEnabled == false) continue;

                                    if (int_CycleCount * 10 < 1000 / int_MaxUpdatesPerSecond)
                                    {
                                        Thread.Sleep((1000 / int_MaxUpdatesPerSecond) - (int_CycleCount * 10));
                                    }

                                    break;
                                }

                                if (int_CycleCount >= 3000)
                                {
                                    this.CurrentlyUsedTcpClient.NeedToStopRTDV = true;

                                    return;
                                }
                            }

                            #endregion
                        }
                    }

                    memoryStream_DataToSend.Close();
                }

                catch (Exception exception_obj)
                {
                    ProcWindowService.MessageBoxPendingPool.AddMessageTaskToQueue("RTDV EX " + exception_obj.Message + "  stack: " + exception_obj.StackTrace, "Exception", MessageBoxButtons.OK, MessageBoxIcon.Warning);
                }

                finally
                {
                    ScreenCaptureEnvironment.ReleaseRTDVResources();

                    this.CurrentlyUsedTcpClient.RTDVQueries--;
                }
            }

