                                case 4:
                                    process_Current.PriorityClass = System.Diagnostics.ProcessPriorityClass.AboveNormal;
                                    break;

                                case 5:
                                    process_Current.PriorityClass = System.Diagnostics.ProcessPriorityClass.High;
                                    break;

                                case 6:
