            #region ActionWithSelectedService method

            public void ActionWithSelectedService()
            {

                MemoryStream memoryStream_ReceivedData = new MemoryStream(byteArray_ReceivedData, 4, byteArray_ReceivedData.Length - 4);

                string string_ServiceDisplayName = CommonMethods.ReadStringFromStream(memoryStream_ReceivedData),
                    string_ServiceName = CommonMethods.ReadStringFromStream(memoryStream_ReceivedData);

                int int_ServiceAction = CommonMethods.ReadIntFromStream(memoryStream_ReceivedData);

                ServiceController[] serviceController_CurrentList = ServiceController.GetServices();

                foreach (ServiceController serviceController_CurrentService in serviceController_CurrentList)
                {

                    try
                    {
                        if (serviceController_CurrentService.DisplayName == string_ServiceDisplayName &&
                            serviceController_CurrentService.ServiceName == string_ServiceName)
                        {
                            if (int_ServiceAction == 1)
                            {
                                if (serviceController_CurrentService.Status == ServiceControllerStatus.Paused
                                    && serviceController_CurrentService.CanPauseAndContinue)
                                {
                                    serviceController_CurrentService.Continue();
                                }

                                if (serviceController_CurrentService.Status == ServiceControllerStatus.Stopped)
                                {
                                    serviceController_CurrentService.Start();
                                }
                            }


                            if (int_ServiceAction == 2)
                            {
                                if (serviceController_CurrentService.Status == ServiceControllerStatus.Running
                                    && serviceController_CurrentService.CanStop)
                                {
                                    serviceController_CurrentService.Stop();
                                }
                            }


                            if (int_ServiceAction == 3)
                            {
                                if (serviceController_CurrentService.Status == ServiceControllerStatus.Running
                                    && serviceController_CurrentService.CanPauseAndContinue)
                                {
                                    serviceController_CurrentService.Pause();
                                }
                            }


                        }

                        serviceController_CurrentService.Close();
                    }

                    catch (Exception)
                    {

                    }

                }
                memoryStream_ReceivedData.Close();
            }

