            public void ReceiveFile()
            {
                MemoryStream memoryStream_ReceivedData = new MemoryStream(byteArray_ReceivedData, 4, byteArray_ReceivedData.Length - 4);

                DatabaseOfUploadingFiles obj_DatabaseOfUploadingFiles = null;

                try
                {

                    int int_UINT = CommonMethods.ReadIntFromStream(memoryStream_ReceivedData);

                    byte[] byteArray_ReceivedFile = CommonMethods.ReadBytesFromStream(memoryStream_ReceivedData);

                    memoryStream_ReceivedData.Close();

                    Int64 int64_ReceivedFileLength = byteArray_ReceivedFile.Length;

                    for (int int_CycleCount = 0; int_CycleCount != DatabaseOfUploadingFiles.UploadingFilesCollectionDB.Count; int_CycleCount++)
                    {
                        obj_DatabaseOfUploadingFiles = DatabaseOfUploadingFiles.UploadingFilesCollectionDB[int_CycleCount];

                        if (obj_DatabaseOfUploadingFiles.UINT == int_UINT) break;

                        if (obj_DatabaseOfUploadingFiles.UINT != int_UINT && int_CycleCount == DatabaseOfUploadingFiles.UploadingFilesCollectionDB.Count)
                            return;

                    }

                    obj_DatabaseOfUploadingFiles.fileStream_UploadingFile.Write(byteArray_ReceivedFile, 0, byteArray_ReceivedFile.Length);

                    if (obj_DatabaseOfUploadingFiles.fileStream_UploadingFile.Length == obj_DatabaseOfUploadingFiles.SizeOfUploadingFile)
                    {
                        obj_DatabaseOfUploadingFiles.fileStream_UploadingFile.Flush();

                        obj_DatabaseOfUploadingFiles.fileStream_UploadingFile.Close();

                        DatabaseOfUploadingFiles.UploadingFilesCollectionDB.Remove(obj_DatabaseOfUploadingFiles);

                        return;
                    }

                    obj_DatabaseOfUploadingFiles.fileStream_UploadingFile.Flush();



                    MemoryStream memoryStream_DataToSend = new MemoryStream();

                    CommonMethods.WriteIntToStream(memoryStream_DataToSend, 12);
                    CommonMethods.WriteIntToStream(memoryStream_DataToSend, 0);
                    CommonMethods.WriteIntToStream(memoryStream_DataToSend, int_UINT);

                    CurrentlyUsedTcpClient.SendData(memoryStream_DataToSend.ToArray(), SentDataType.ApplicationData);
                }

                catch (IOException)
                {
                    MemoryStream memoryStream_DataToSend = new MemoryStream();

                    CommonMethods.WriteIntToStream(memoryStream_DataToSend, 12);
                    CommonMethods.WriteIntToStream(memoryStream_DataToSend, -2);

                    CurrentlyUsedTcpClient.SendData(memoryStream_DataToSend.ToArray(), SentDataType.ApplicationData);

                    memoryStream_DataToSend.Close();

                    if (obj_DatabaseOfUploadingFiles != null && obj_DatabaseOfUploadingFiles.fileStream_UploadingFile != null)
                    {
                        obj_DatabaseOfUploadingFiles.fileStream_UploadingFile.Close();
                    }
                }
                finally
                {
                    memoryStream_ReceivedData.Close();
                }

            }
