                    int int_UINT = CommonMethods.ReadIntFromStream(memoryStream_ReceivedData),
                        int_SendedSegmentSize = CommonMethods.ReadIntFromStream(memoryStream_ReceivedData);

                    DatabaseOfSendingFiles databaseOfSendingFile_CurrentElement = new DatabaseOfSendingFiles();

