                    Int64 int64_SizeOfSendingFragment = 0;

                    if (databaseOfSendingFile_CurrentElement.fileStream_SendingFile.Length - databaseOfSendingFile_CurrentElement.fileStream_SendingFile.Position > int_SendedSegmentSize)
                        int64_SizeOfSendingFragment = int_SendedSegmentSize;

                    else int64_SizeOfSendingFragment = (databaseOfSendingFile_CurrentElement.fileStream_SendingFile.Length - databaseOfSendingFile_CurrentElement.fileStream_SendingFile.Position);

