                    if (!Directory.Exists(string_Path)) return;

                    #region collect directory items

                    DirectoryInfo directoryInfo_NecsessaryDir = new DirectoryInfo(string_Path);

                    DirectoryInfo[] directoryInfoArrray_CurrentDir = directoryInfo_NecsessaryDir.GetDirectories();

                    CommonMethods.WriteIntToStream(memoryStream_DataToSend, directoryInfoArrray_CurrentDir.Length);

                    foreach (DirectoryInfo directoryInfo_CurrentDir in directoryInfoArrray_CurrentDir)
