                    #endregion

                    #region collect file items

                    FileInfo[] fileInfoArrray_CurrentFilesArray = directoryInfo_NecsessaryDir.GetFiles();

                    CommonMethods.WriteIntToStream(memoryStream_DataToSend, fileInfoArrray_CurrentFilesArray.Length);

                    foreach (FileInfo fileInfo_CurrentFile in fileInfoArrray_CurrentFilesArray)
