                        CommonMethods.WriteInt64ToStream(memoryStream_DataToSend, fileInfo_CurrentFile.Length);

                        CommonMethods.WriteStringToStream(memoryStream_DataToSend, string_SendingItemModifiedDate);

                        CommonMethods.WriteStringToStream(memoryStream_DataToSend, fileInfo_CurrentFile.Attributes.ToString());

