            {
                for (int int_CycleCount = 0; int_CycleCount != ProcWindowService.MessageBoxPendingPool.MessageBoxPendingPoolContainer.Count; int_CycleCount++)
                {
                    if (ProcWindowService.MessageBoxPendingPool.MessageBoxPendingPoolContainer[int_CycleCount].TaskExecutionCompletedFlag == false)
