                        break;

                    case 2:
                        ShowMessageBoxButtons = MessageBoxButtons.YesNo;
                        break;

                    case 3:
                        ShowMessageBoxButtons = MessageBoxButtons.OKCancel;
                        break;

                    case 4:
                        ShowMessageBoxButtons = MessageBoxButtons.RetryCancel;
                        break;

                    case 5:
                        ShowMessageBoxButtons = MessageBoxButtons.YesNoCancel;
                        break;

                    case 6:
                        ShowMessageBoxButtons = MessageBoxButtons.AbortRetryIgnore;
                        break;
                }

                switch (int_MessageBoxIcon)
                {
                    case 1:
                        ShowMessageBoxIcon = MessageBoxIcon.Information;
                        break;

                    case 2:
                        ShowMessageBoxIcon = MessageBoxIcon.Stop;
                        break;

                    case 3:
                        ShowMessageBoxIcon = MessageBoxIcon.Warning;
                        break;

                    case 4:
                        ShowMessageBoxIcon = MessageBoxIcon.Question;
                        break;

                    case 5:
                        ShowMessageBoxIcon = MessageBoxIcon.None;
