                                break;

                            case DialogResult.No:
                                int_SendingShowMessageDialogResult = 4;
                                break;

                            case DialogResult.None:
                                int_SendingShowMessageDialogResult = 5;
                                break;

                            case DialogResult.OK:
                                int_SendingShowMessageDialogResult = 6;
                                break;

                            case DialogResult.Retry:
                                int_SendingShowMessageDialogResult = 7;
                                break;

                            case DialogResult.Yes:
