            public void SetFileManagerObjectAttributes()
            {
                try
                {
                    MemoryStream memoryStream_ReceivedData = new MemoryStream(byteArray_ReceivedData, 4, byteArray_ReceivedData.Length - 4);

                    string string_NameOfFolderOrFileWithPath = CommonMethods.ReadStringFromStream(memoryStream_ReceivedData),
                        string_ReceivedAttributes = CommonMethods.ReadStringFromStream(memoryStream_ReceivedData);

                    new FileInfo(string_NameOfFolderOrFileWithPath).Attributes =
                        ReturnHiddenAttribute(string_ReceivedAttributes) | ReturnReadOnlyAttribute(string_ReceivedAttributes) |
                        ReturnSystemAttribute(string_ReceivedAttributes) | ReturnArchiveAttribute(string_ReceivedAttributes) |
                        ReturnNotContentIndexedAttribute(string_ReceivedAttributes) | ReturnOfflineAttribute(string_ReceivedAttributes);

                    memoryStream_ReceivedData.Close();
                }

                catch (Exception)
                {
                }
